import React, { Component } from 'react';
import './App.css';


/*import:
  "dumb component"
  "functional stateless component (FSC)"
  "presentational component" 
  ^^^^^All different names for the same thing*/
import Form from "../components/Form";


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: "Joe",
    };
  }

  render() {
    const { userName } = this.state;
    //^ this is called destructuring. Cleaner code so you dont have to repeat this.props for everything.

    return (
      <div className="container">

      {/*Presentational Component(PC)*/}
      <Form 
        userName={userName} />
        {/*^^^^Passing userName "Joe" down to PC*/}
      </div>

    );
  }
}

export default App;
