import React, { Component } from 'react';
import './App.css';


/*import:
  "dumb component"
  "functional stateless component (FSC)"
  "presentational component" 
  ^^^^^All different names for the same thing.  They don't manage state*/
import Form from "../components/Form";


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: "Joe",
    };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ userName: event.target.value });
    console.log(this.state.userName)
  };

  handleSubmit() {
    console.log(this.state.userName)
  };


  render() {
    const { userName } = this.state;
    //^ this is called destructuring. Cleaner code so you dont have to repeat this.props for everything.

    return (
      <div className="container">

      {/*Presentational Component(PC)*/}
      <Form 
        userName={userName} 
        handleChange={this.handleChange}
        handleSubmit={this.handleSubmit}
      />
        {/*^^^^Passing userName "Joe" and functions down to PC*/}
      </div>

    );
  }
}

export default App;
